'use strict';

var path = require('path');

var current = process.cwd();
var desired = path.dirname(require.main.filename);

if(current !== desired){
  try{
    process.chdir(desired);
  } catch (err) {
    console.log('Can\'t change the current working directory. Please ensure you have sufficient permissios');
    process.exit(1);
  }
}

module.exports = desired;
